"use strict";window.onload=function(){$.ajax({url:"../lib/nav_top.json",dataType:"json",success:function(t){console.log(t);var o="";t.forEach(function(n){o+="<li>".concat(n.name,"</li>")}),$(".nav_top  ul").html(o).on({mouseenter:function(){return $(".nav_box").stop().slideDown()},mouseleave:function(){return $(".nav_box").stop().slideUp()}}).children("li").on("mouseover",function(){var n=$(this).index(),o=t[n].list,i="";o.forEach(function(n){i+='\n                  <li>\n                    <div>\n                      <img src="'.concat(n.list_url,'" alt="">\n                    </div>\n                    <p class="title">').concat(n.list_name,'</p>\n                    <span class="price">').concat(n.list_price,"</span>\n                  </li>\n                ")}),$(".nav_box > ul").html(i)}),$(".nav_box").on({mouseover:function(){$(this).finish().show()},mouseout:function(){$(this).finish().slideUp()}})}});new Swiper(".swiper-container",{loop:!0,autoplay:{delay:2e3},pagination:{el:".swiper-pagination"},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}})};